function getLatLongFromAddress($address) {\n    $api_key = "YOUR_GOOGLE_MAPS_API_KEY";\n    $url = sprintf("https://maps.googleapis.com/maps/api/geocode/json?address=%s&key=%s", urlencode($address), $api_key);\n\n    $response = file_get_contents($url);\n    if ($response === FALSE) {\n        return false;\n    }\n\n    $data = json_decode($response, true);\n    if (isset($data['results'][0]['geometry']['location'])) {\n        $lat = $data['results'][0]['geometry']['location']['lat'];\n        $lng = $data['results'][0]['geometry']['location']['lng'];\n        return ['lat' => $lat, 'lng' => $lng];\n    } else {\n        return false;\n    }\n}\n\n// Example usage\n$address = "London, UK";\n$coordinates = getLatLongFromAddress($address);\nif ($coordinates) {\n    echo "Latitude: " . $coordinates['lat'] . ", Longitude: " . $coordinates['lng'];\n} else {\n    echo "Could not retrieve coordinates.";\n}