<?php\n// Assuming $pdo is your PDO database connection object\n$query = "SELECT color, content FROM table_name";\n$stmt = $pdo->query($query);\n?>\n\n<table>\n    <tr>\n        <?php foreach ($stmt as $row): ?>\n            <td style="background-color: <?= htmlspecialchars($row['color']); ?>">\n                <?= htmlspecialchars($row['content']); ?>\n            </td>\n        <?php endforeach; ?>\n    </tr>\n</table>