$result = mysql_query("SHOW TABLES FROM feather") or die(mysql_error());\n\n$table_list = []; // Initialize $table_list as an array\n\nwhile (list($table) = mysql_fetch_row($result)) {\n    $sqlA = "SELECT COUNT(*) FROM `$table` WHERE `site` LIKE '$entry'";\n    $resA = mysql_query($sqlA) or die("$sqlA:" . mysql_error());\n    list($isThere) = mysql_fetch_row($resA);\n    $isThere = intval($isThere);\n    if ($isThere) {\n        $table_list[] = $table;\n    }\n}\n\n// Check if $table_list is not empty\nif (!empty($table_list)) {\n    foreach ($table_list as $table) {\n        $sql = "SELECT votes_up FROM `$table` WHERE `site` LIKE '$entry'";\n        $sql1 = mysql_query($sql) or die("$sql:" . mysql_error());\n        while ($row = mysql_fetch_assoc($sql1)) {\n            $votes[$table] = $row['votes_up'];\n            $sum += $row['votes_up'];\n        }\n    }\n} else {\n    // Handle the case where no tables matched\n    print "";\n}