<?php\n\n$input = [\n    ['id' => 1, 'name' => 'John00', 'value' => 0.4],\n    ['id' => 2, 'name' => 'Ann01', 'value' => 0.001],\n    ['id' => 1, 'name' => 'John00', 'value' => 0.2],\n    ['id' => 3, 'name' => 'Ann', 'value' => 0.1],\n    ['id' => 2, 'name' => 'Ann01', 'value' => 0.01],\n    ['id' => 1, 'name' => 'John00', 'value' => 0.3],\n];\n\n$result = [];\n\nforeach ($input as $row) {\n    $id = $row['id'];\n    if (!isset($result[$id])) {\n        $result[$id] = [\n            'id' => $id,\n            'name' => $row['name'],\n            'value' => 0,\n        ];\n    }\n    $result[$id]['value'] += $row['value'];\n}\n\n$result = array_values($result); // To reindex the array\n\nprint_r($result);\n?>