<?php\n\n// Associative array of IDs with names\n$associativeArray = [\n    1 => 'Alice',\n    2 => 'Bob',\n    3 => 'Charlie',\n    // ... more entries\n];\n\n// Array of IDs to compare against\n$idList = [2, 4, 5];\n// Convert it to an associative array with null values for comparison\n$idsToCheck = array_flip($idList);\n\n// Find the intersection using array_intersect_key\n$result = array_intersect_key($associativeArray, $idsToCheck);\n\nprint_r($result);\n?>