if (!empty($_FILES['addFile']['name'])) { // Check if files were uploaded\n    $fileCount = count($_FILES['addFile']['name']); // Get the number of uploaded files\n\n    for ($i = 0; $i < $fileCount; $i++) {\n        $fileToCopy = $_FILES["addFile"]["tmp_name"][$i];\n        $fileName   = $_FILES["addFile"]["name"][$i];\n\n        if (!empty($fileToCopy)) { // Check if the file was uploaded without issues\n            $copyTo = $baseDir . "/" . basename($fileName); // Use basename() to prevent directory traversal attacks\n\n            if (@copy($fileToCopy, $copyTo) === false) {\n                die("Cannot copy " . htmlspecialchars($fileToCopy) . " to " . htmlspecialchars($copyTo));\n            }\n        }\n    }\n}