// DataAccessObject.php\nclass DataAccessObject {\n    protected $adapter;\n\n    public function __construct(Zend_Db_Adapter_Abstract $adapter) {\n        $this->adapter = $adapter;\n    }\n\n    public function getPaginatorAdapter() {\n        $select = $this->prepareSelectQuery();\n        return new Zend_Paginator_Adapter_DbSelect($select);\n    }\n\n    protected function prepareSelectQuery() {\n        // Prepare your DB select query here\n        $select = $this->adapter->select()\n            ->from('your_table', array('*'))\n            ->join(...); // Add your join logic\n\n        return $select;\n    }\n}\n\n// YourModel.php\nclass YourModel {\n    private $dao;\n\n    public function __construct(DataAccessObject $dao) {\n        $this->dao = $dao;\n    }\n\n    public function getPaginator($page, $itemsPerPage) {\n        $paginatorAdapter = $this->dao->getPaginatorAdapter();\n        $paginator = new Zend_Paginator($paginatorAdapter);\n        $paginator->setCurrentPageNumber($page)\n                  ->setItemCountPerPage($itemsPerPage);\n\n        return $paginator;\n    }\n}\n\n// Controller.php\nclass YourController extends Zend_Controller_Action {\n    public function yourAction() {\n        // Assume you have a method to get the DB adapter\n        $dbAdapter = $this->_getDBConnection();\n        \n        // Create DAO and Model instances\n        $dao = new DataAccessObject($dbAdapter);\n        $model = new YourModel($dao);\n\n        // Get paginator and pass it to view\n        $paginator = $model->getPaginator((int)$this->_getParam('page', 1), 20);\n        $this->view->paginator = $paginator;\n    }\n}