require_once('nusoap.php');\n\n$client = new nusoap_client("my.wsdl", true);\n\n// Define namespaces\n$ns1 = 'http://domain.tld/namespace1';\n$ns2 = 'http://domain.tld/namespace2';\n\n// Create elements with soapval and specify the namespace\n$firstname = new soapval('Firstname', 'xsd:string', 'Thomas', false, $ns2);\n$person = new soapval('Person', false, array($firstname), false, $ns2);\n\n$attribute = new soapval('Attribute', 'xsd:string', 'foo', false, $ns1);\n\n// Combine into the main operation\n$myOperation = new soapval('myOperation', false, array($person, $attribute), false, $ns1);\n\n// Prepare parameters for call\n$params = array(\n    new soapval('queryCCApplicationDataRequest', false, array($myOperation), false, '')\n);\n\n// Call method\n$result = $client->call('myOperation', $params, '', 'soapAction');\n\nif ($client->fault) {\n    echo 'Fault: <p/>';\n    print_r($result);\n} else {\n    $err = $client->getError();\n    if ($err) {\n        echo 'Error: ' . $err;\n    } else {\n        echo 'Result: ';\n        print_r($result);\n    }\n}