<?php\n// Assuming you have a MySQL connection setup already: $conn\n\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    // Sanitize inputs\n    $info = htmlspecialchars($_POST['info']);\n    $rowId = (int)$_POST['row_id'];\n\n    // Update the database for the specific row ID\n    $stmt = $conn->prepare("UPDATE your_table SET info_column = ? WHERE id = ?");\n    $stmt->bind_param("si", $info, $rowId);\n\n    if ($stmt->execute()) {\n        echo "Record updated successfully";\n    } else {\n        echo "Error updating record: " . $stmt->error;\n    }\n\n    $stmt->close();\n}\n?>