class Registration {\n    private static $dates;\n\n    public function __construct() {\n        self::initializeDates();\n    }\n\n    private static function initializeDates() {\n        self::$dates = array(\n            'start' => mktime(0,  0,  0, 7, 30, 2009),\n            'end'   => mktime(0,  0,  0, 8,  2, 2009),\n            'close' => mktime(23, 59, 59, 7, 20, 2009),\n            'early' => mktime(0,  0,  0, 3, 19, 2009),\n        );\n    }\n\n    // Method to get dates\n    public static function getDates() {\n        return self::$dates;\n    }\n}\n\n// Usage example\n$registration = new Registration();\nprint_r($registration::getDates());