$mainsection = "auth"; // The name of the table\n$username = $user; // Make sure $user is sanitized\n\n// Create a connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$query1 = "SELECT * FROM auth WHERE username='$username'";\n$result = $conn->query($query1);\n\nif ($result->num_rows > 0) {\n    // Output data of each row\n    while($thisrow = $result->fetch_assoc()) {\n        echo "Username: " . htmlspecialchars($thisrow['username']) . \n             " - Study: " . htmlspecialchars($thisrow['report']) . "<br>";\n    }\n} else {\n    echo "0 results";\n}\n\n$conn->close();