class My_Form_Upload extends Zend_Form\n{\n    public function init()\n    {\n        $file = new Zend_Form_Element_File('file');\n        $file->setLabel('Attach File:')\n             ->setRequired(true)\n             ->addValidator('NotEmpty')\n             ->addValidator('Extension', false, 'jpg,png');\n\n        $this->addElement($file);\n\n        // Add other elements\n        $name = new Zend_Form_Element_Text('username');\n        $name->setLabel('Username:');\n        $name->setFilters(['StripTags', 'StringTrim'])\n             ->addValidator('NotEmpty')\n             ->setValue(isset($_POST['username']) ? $_POST['username'] : '');\n\n        $this->addElement($name);\n\n        // Add submit button\n        $submit = new Zend_Form_Element_Submit('submit');\n        $submit->setLabel('Upload File');\n\n        $this->addElement($submit);\n    }\n}