<?php\n$args = array(\n    'tax_query' => array(\n        array(\n            'taxonomy' => 'technologies',\n            'field'    => 'slug',\n            'terms'    => array('php', 'sql'),\n            'operator' => 'IN', // Use 'OR' to show posts with any of the terms, 'AND' for all\n        ),\n    ),\n);\n\n$query = new WP_Query( $args );\n\nif ( $query->have_posts() ) {\n    while ( $query->have_posts() ) : $query->the_post();\n        echo '<h2>' . get_the_title() . '</h2>';\n        // other stuff...\n    endwhile;\n} else {\n    echo 'No posts found';\n}\nwp_reset_postdata();\n?>