<?php\n$scriptDirectory = __DIR__; // This gives the absolute path of the current script\n$fileDirectory = $scriptDirectory . '/fileDirectory/';\n$filePath = $fileDirectory . $fileName;\n\n// Ensure the directory exists, create it if not\nif (!is_dir($fileDirectory)) {\n    mkdir($fileDirectory, 0755, true); // Create the directory recursively\n}\n\n// Write to file\nif (file_put_contents($filePath, $fileContents) === false) {\n    die("Error writing to file $filePath");\n}\n?>