function createSecureShortHash($input) {\n    $hash = sha1($input); // Generate a SHA-1 hash\n    $base62Chars = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';\n    \n    // Convert the hash to a base62 string\n    $base62String = '';\n    for ($i = 0; $i < strlen($hash); $i += 5) {\n        $chunk = substr($hash, $i, 5);\n        $decValue = hexdec($chunk);\n        $base62String .= $base62Chars[$decValue % 62];\n    }\n    \n    return substr($base62String, -8); // Get last 8 characters\n}\n\n// Example usage:\n$shortHashSecure = createSecureShortHash($inputString);\necho $shortHashSecure;