$requestMethod = $_SERVER['REQUEST_METHOD'];\n\nswitch ($requestMethod) {\n    case 'GET':\n        // Handle GET request\n        break;\n    case 'POST':\n        // Handle POST request\n        break;\n    case 'PUT':\n        // Handle PUT request\n        // Note: PUT data is not available in $_POST or $_REQUEST, you may need to read from php://input\n        $putData = file_get_contents("php://input");\n        parse_str($putData, $putVars);\n        break;\n    case 'DELETE':\n        // Handle DELETE request\n        // Similar to PUT, data might be sent in the message body and needs to be read from php://input\n        $deleteData = file_get_contents("php://input");\n        parse_str($deleteData, $deleteVars);\n        break;\n    default:\n        // Invalid request method\n        http_response_code(405); // Method Not Allowed\n        echo "Invalid Request Method";\n        break;\n}