<?php\n   namespace App\Repository;\n\n   use Doctrine\ORM\EntityRepository;\n\n   class UserRepository extends EntityRepository {\n       public function createUser($first, $last, $email) {\n           $user = new User();\n           $user->first = $first;\n           $user->last = $last;\n           $user->email = $email;\n\n           $entityManager = $this->_em;\n           $entityManager->persist($user);\n           $entityManager->flush();\n\n           return $user;\n       }\n   }