class IndexController extends Zend_Controller_Action {\n    public function __call($action, $args) {\n        // Format action name as a view script file: e.g., booksmoreAction -> books-more.phtml\n        $viewScript = strtolower(implode('-', preg_split('/(?=[A-Z])/', substr($action, 0, -6)))) . '.phtml';\n\n        // Set the view script path and check if it exists\n        $scriptPath = APPLICATION_PATH . '/views/scripts/index/' . $viewScript;\n        \n        if (!file_exists($scriptPath)) {\n            // Handle missing action or page gracefully\n            $this->getResponse()->setHttpResponseCode(404);\n            $this->_forward('error', 'index'); // Forward to a generic error action or 404 handler\n            return;\n        }\n\n        // Render the view script\n        $this->_helper->viewRenderer->setScriptAction($action);\n    }\n}