<?php\n$memcache = new Memcache;\nif (!$memcache->connect("localhost", 11211)) {\n    die("Could not connect to memcached server");\n}\n\nfunction outData($getList) global $memcache; {\n    global $memcache; // Declare $memcache as a global variable\n    $key = md5($getList);\n    $memVal = $memcache->get($key);\n\n    if ($memVal === false) { // Check if the value is not found in memcache\n        $results = mysql_query($getList) or die(mysql_error());\n\n        // Assuming you process your results here to create $memVal\n        while ($row = mysql_fetch_assoc($results)) {\n            // Process your data and append it to $memVal\n            $memVal .= "Your processed data"; // Replace with actual processing\n        }\n\n        // Store the result in memcache with a 60-second expiration time (not 60000 seconds)\n        $memcache->set($key, $memVal, false, 60);\n    }\n    return $memVal;\n}\n?>