$names = [];\nif (isset($_POST['first'])) {\n    $names[] = $_POST['first'];\n}\nif (isset($_POST['second'])) {\n    $names[] = $_POST['second'];\n}\nif (isset($_POST['third'])) {\n    $names[] = $_POST['third'];\n}\nif (isset($_POST['fourth'])) {\n    $names[] = $_POST['fourth']; // Corrected from '$_POST['second']'\n}\n\n$str = '';\nif (count($names) > 1) {\n    $last = array_pop($names); // Remove the last element\n    $str .= implode(', ', $names) . ' and ' . $last;\n} elseif (count($names) == 1) {\n    $str .= $names[0];\n}\n\necho $str; // Outputs: Joe, Adam, and Mike or just names if less than three are provided.