class CustomSoapClient extends Zend_Soap_Client {\n    protected function _doRequest(Zend_Http_Client $client, $request, $location, $action, $version, $one_way = null) {\n        // Save the request to a file\n        $filePath = '/path/to/save/soap_request.xml';\n        file_put_contents($filePath, $request . PHP_EOL, FILE_APPEND);\n\n        // If you want to prevent the actual request from being sent, return an empty response.\n        // Otherwise, you can continue with the sending process if needed.\n        return ''; // Returning an empty string as we're not actually sending the request\n    }\n}\n\n// Usage example\n$wsdlUrl = 'http://your-soap-service.com/service?wsdl';\n$client = new CustomSoapClient($wsdlUrl);\n$response = $client->YourFunctionName(array('param1' => 'value1'));