<?php\n// Start this script before starting any session\n\n$serverName = $_SERVER['SERVER_NAME'];\n\nif (strpos($serverName, 'www.example.com') !== false) {\n    $cookieDomain = '.example.com';\n} elseif (strpos($serverName, 'www.anotherdomain.com') !== false) {\n    $cookieDomain = '.anotherdomain.com';\n} else {\n    // Default or catch-all\n    $cookieDomain = '.' . $serverName;\n}\n\n// Set the cookie domain for sessions\nsession_set_cookie_params(0, '/', $cookieDomain);