function renderIterative($startIndex) {\n    $stack = [$startIndex];\n    \n    while (!empty($stack)) {\n        $index = array_pop($stack);\n        \n        foreach ($things[$index] as $key => $data) {\n            echo '<div>';\n            // Place here any irrelevant operations you need to perform\n            if (isset($data['id'])) {\n                echo '<div class="wrap">';\n                \n                // Push this index onto the stack for later rendering\n                array_push($stack, $things[$data['id']]);\n                \n                echo '</div>';\n            }\n            \n            echo '</div>';\n        }\n    }\n}