<?php\nfunction safeExecute($command) {\n    // Sanitize the command using escapeshellcmd and escapeshellarg\n    $safeCommand = escapeshellcmd($command);\n    \n    // Execute the sanitized command\n    $output = shell_exec($safeCommand);\n    \n    return $output;\n}\n\n// Example usage\n$userInput = 'echo "Hello, World!"';\n$result = safeExecute($userInput);\necho "<pre>$result</pre>";\n?>