<?php\nheader('Content-Type: application/json');\n\nif(isset($_GET['score'])) {\n    $score = intval($_GET['score']); // Convert score to integer and sanitize input\n\n    // Assuming $conn is your database connection object\n    $stmt = $conn->prepare("INSERT INTO score (score_count) VALUES (?)");\n    $stmt->bind_param("i", $score);\n    if($stmt->execute()) {\n        echo json_encode(['success' => true, 'message' => 'Vote recorded successfully']);\n    } else {\n        echo json_encode(['success' => false, 'error' => 'Failed to record vote']);\n    }\n    $stmt->close();\n} else {\n    echo json_encode(['success' => false, 'error' => 'No score provided']);\n}\nexit;\n?>