<?php\n\nfunction getPeopleByIds($ids) {\n    // Connect to your MySQL database\n    $pdo = new PDO('mysql:host=localhost;dbname=test', 'user', 'password');\n\n    // Create a string of placeholders for the IN() clause, e.g., ?, ?, ?\n    $placeholders = implode(', ', array_fill(0, count($ids), '?'));\n\n    // Prepare the SQL query with dynamically created placeholders\n    $sql = "SELECT `age`, `name` FROM `people` WHERE id IN ($placeholders)";\n    $stmt = $pdo->prepare($sql);\n\n    // Execute the statement with an array of IDs\n    $stmt->execute($ids);\n\n    // Fetch results\n    return $stmt->fetchAll(PDO::FETCH_ASSOC);\n}\n\n// Example usage\n$ids = [12, 45, 65, 33];\n$results = getPeopleByIds($ids);\nprint_r($results);\n\n?>