function executeFunction($functionToBeCalled, $additional_params) {\n    // Ensure that additional_params is an array\n    if (!is_array($additional_params)) {\n        $additional_params = array($additional_params);\n    }\n    \n    // Call the function with the provided parameters\n    call_user_func_array($functionToBeCalled, $additional_params);\n}\n\n// Example usage:\n\nfunction exampleFunction($param1, $param2) {\n    echo "Function called with params: $param1 and $param2";\n}\n\nexecuteFunction('exampleFunction', array('Hello', 'World'));