<?php\n\nfunction getObject($key) {\n    $cachedObject = apcu_fetch($key);\n\n    if ($cachedObject === false) {\n        // Create the object and store it in cache if it doesn't exist\n        $cachedObject = new ErrorHandler(); // Assuming ErrorHandler is your class\n        apcu_store($key, $cachedObject);\n    }\n\n    return $cachedObject;\n}\n\n// Usage\n$errorHandler = getObject('error_handler_instance');