function renderTemplate($template, $data) {\n    // Simple tag replacement logic\n    foreach ($data as $key => $value) {\n        $placeholderStart = "{continue}";\n        $placeholderEnd = "{/continue}";\n\n        if (isset($data[$key]['canContinue']) && $data[$key]['canContinue']) {\n            $template = str_replace("{$placeholderStart}" . $key . "{$placeholderEnd}", $key, $template);\n        } else {\n            $template = preg_replace("/\\{continue\\}.*?\\{$key\\}.*?\\{/continue\\}/s", "", $template);\n        }\n    }\n\n    return $template;\n}\n\n// Example usage\n$template = "Here is the intro. {continue}click to continue{/continue}";\n$data = [\n    "{continue}" => ["canContinue" => true]\n];\n\necho renderTemplate($template, $data);