<?php\n$uploaddir = '/path/to/upload/directory/';\n$filename = $_FILES['file']['name'];\n// Replace spaces with underscores\n$safe_filename = str_replace(' ', '_', $filename);\n\nif (move_uploaded_file($_FILES['file']['tmp_name'], $uploaddir . $safe_filename)) {\n    // uploaded file was moved and renamed successfully. Display a message.\n    $file_url = "http://example.org/" . rawurlencode($safe_filename);\n    echo "Link: <a href='mailto:?body={$file_url}'>Send via Email</a>";\n} else {\n    echo "There was an error uploading the file.";\n}\n?>