<?php\n\nfunction itstime() {\n    // Your logic here\n    $logMessage = "Log entry at: " . date("Y-m-d H:i:s") . "\n";\n    \n    // Write to log file\n    file_put_contents('/path/to/your/logfile.log', $logMessage, FILE_APPEND);\n    \n    // Email sending logic (simplified)\n    $to      = 'example@example.com';\n    $subject = 'Hourly Update';\n    $message = 'This is your hourly update.';\n    $headers = 'From: webmaster@example.com' . "\r\n" .\n               'Reply-To: webmaster@example.com' . "\r\n" .\n               'X-Mailer: PHP/' . phpversion();\n    \n    if(mail($to, $subject, $message, $headers)) {\n        echo "Email sent successfully.";\n    } else {\n        echo "Failed to send email.";\n    }\n}\n\n// Call the function for demonstration purposes\nitstime();\n\n?>