<?php\nfunction validateForm($data) {\n    $errors = [];\n\n    // Check if all fields are filled\n    foreach ($data as $key => $value) {\n        if (!isset($value) || trim($value) === '') {\n            $errors[$key] = ucfirst($key) . " is required.";\n        }\n    }\n\n    // Validate general text (only letters and spaces)\n    if (!empty($data['general_text']) && !preg_match("/^[a-zA-Z ]*$/", $data['general_text'])) {\n        $errors['general_text'] = "Invalid general text format.";\n    }\n\n    // Validate username (alphanumeric with underscores)\n    if (!empty($data['username']) && !preg_match("/^[a-zA-Z0-9_]*$/", $data['username'])) {\n        $errors['username'] = "Username can only contain letters, numbers, and underscores.";\n    }\n\n    // Validate password (at least 8 characters including one letter and one number)\n    if (!empty($data['password']) && !preg_match("/^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,}$/", $data['password'])) {\n        $errors['password'] = "Password must be at least 8 characters long and include both letters and numbers.";\n    }\n\n    // Validate date (YYYY-MM-DD)\n    if (!empty($data['date']) && !preg_match("/^\d{4}-\d{2}-\d{2}$/", $data['date'])) {\n        $errors['date'] = "Date format is invalid. Use YYYY-MM-DD.";\n    }\n\n    return $errors;\n}\n\n// Example usage\n$data = [\n    'general_text' => $_POST['general_text'],\n    'username' => $_POST['username'],\n    'password' => $_POST['password'],\n    'date' => $_POST['date']\n];\n\n$errors = validateForm($data);\n\nif (!empty($errors)) {\n    foreach ($errors as $error) {\n        echo "<p style='color:red;'>$error</p>";\n    }\n} else {\n    echo "<p>Form is valid!</p>";\n}\n?>