$pdo = new PDO('mysql:host=localhost;dbname=database', 'username', 'password');\n\n// Prepare and execute SELECT statement\n$stmtSelect = $pdo->prepare("SELECT itemid, points FROM items WHERE id=:id");\n$stmtSelect->execute([':id' => 1]);\n\n// Execute UPDATE statement\n$stmtUpdate = $pdo->prepare("UPDATE accounts SET userpoints=:points");\n$stmtUpdate->execute([':points' => 1000]);