require_once 'Zend/Search/Lucene.php';\nrequire_once 'Zend/Pdf.php';\n\nfunction pdfToText($pdfFilePath) {\n    $pdf = Zend_Pdf::load($pdfFilePath);\n    $text = '';\n    foreach ($pdf->getPages() as $page) {\n        $text .= $page->getText();\n    }\n    return $text;\n}\n\n// Path to your PDF file\n$pdfPath = '/path/to/your/document.pdf';\n\n// Extract text from the PDF\n$textContent = pdfToText($pdfPath);\n\n// Create or open an existing index\n$indexPath = '/path/to/index';\nif (!file_exists($indexPath)) {\n    Zend_Search_Lucene::create($indexPath);\n}\n$index = Zend_Search_Lucene::open($indexPath);\n\n// Create a new document and add fields to it\n$doc = new Zend_Search_Lucene_Document();\n$doc->addField(Zend_Search_Lucene_Field::text('content', $textContent));\n$doc->addField(Zend_Search_Lucene_Field::unIndexed('path', $pdfPath));\n\n// Add the document to the index\n$index->addDocument($doc);\n$index->commit();\n\n// Searching the index\n$query = Zend_Search_Lucene_Query_Phrase::parse('search phrase');\n$results = $index->find($query);\n\nforeach ($results as $result) {\n    echo "Found: {$result->path}\n";\n}