<?php\nsession_start();\n\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    if (isset($_FILES['csvfile'])) {\n        $fileTmpPath = $_FILES['csvfile']['tmp_name'];\n        \n        // Read the file and store its contents in an array\n        $fileData = [];\n        if (($handle = fopen($fileTmpPath, "r")) !== FALSE) {\n            while (($row = fgetcsv($handle, 1000, ",")) !== FALSE) {\n                $fileData[] = $row;\n            }\n            fclose($handle);\n        }\n\n        // Store the file data in session\n        $_SESSION['upload_data'] = $fileData;\n\n        // Redirect to the next page for column mapping and database insertion\n        header("Location: confirm_and_store.php");\n        exit();\n    }\n}\n\n// Show upload form\n?>\n<form method="post" enctype="multipart/form-data">\n    <input type="file" name="csvfile" accept=".csv"/>\n    <button type="submit">Upload</button>\n</form>