class ModuleController {\n       protected $modules = [];\n\n       public function addModule(ModuleInterface $module) {\n           $this->modules[$module->getName()] = $module;\n       }\n\n       public function execute($moduleName, $action, $params) {\n           if (isset($this->modules[$moduleName])) {\n               return call_user_func_array([$this->modules[$moduleName], $action], $params);\n           }\n           throw new Exception("Module or action not found");\n       }\n   }