$created_time = 'Thu, 18 Jun 2009 21:44:49 +0000';\n$utc_offset = -28800; // This is in seconds\n\n// Create a DateTime object for the created time\n$dateTime = new DateTime($created_time);\n\n// Convert UTC offset to hours and minutes for time zone adjustment\n$offsetHours = floor(abs($utc_offset) / 3600);\n$offsetMinutes = abs($utc_offset) % 3600 / 60;\n$timezoneString = ($utc_offset >= 0 ? '+' : '-') . sprintf('%02d:%02d', $offsetHours, $offsetMinutes);\n\n// Set the desired timezone\n$userTimezone = new DateTimeZone($timezoneString);\n$dateTime->setTimezone($userTimezone);\n\n// Format the date for output\necho $dateTime->format('l jS \o\f F Y h:i:s A'); // Output: Thursday 18th of June 2009 02:44:49 PM