<?php\nrequire_once('path/to/PclZip.php');\n\n// Create an instance of the PclZip class\n$archive = new PclZip('example.zip');\n\n// Add files to the zip archive\n$v_list = $archive->add(\n    array(\n        'file1.txt' => '',\n        'dir/file2.txt' => ''\n    ),\n    PCLZIP_OPT_REMOVE_PATH, 'source_directory'\n);\n\nif ($v_list == 0) {\n    die("Error : " . $archive->errorInfo(true));\n}\n?>