<?php\n\nfunction compressPDF($inputPath, $outputPath) {\n    // Command to run Ghostscript\n    $command = "gs -sDEVICE=pdfwrite -dCompatibilityLevel=1.4 ".\n               "-dNOPAUSE -dQUIET -dBATCH -dColorImageDownsampleType=/Averaging ".\n               "-dColorImageResolution=150 -dMonoImageDownsampleType=/Averaging ".\n               "-dMonoImageResolution=150 -sOutputFile=\"$outputPath\" \"$inputPath\"";\n    \n    // Execute the command\n    exec($command, $output, $returnVar);\n    \n    if ($returnVar === 0) {\n        echo "PDF compressed successfully!\n";\n    } else {\n        echo "An error occurred while compressing the PDF.\n";\n    }\n}\n\n// Usage example\n$inputFilePath = '/path/to/input.pdf';\n$outputFilePath = '/path/to/output_compressed.pdf';\n\ncompressPDF($inputFilePath, $outputFilePath);\n\n?>