class MySQL {\n    private $conn;\n\n    function __construct() {\n        $this->conn = new mysqli(DB_SERVER, DB_USER, DB_PASSWORD, DB_NAME);\n        if ($this->conn->connect_error) {\n            trigger_error('Error connecting to host: ' . $this->conn->connect_error, E_USER_ERROR);\n        }\n    }\n\n    function extracting_data($table, $fields, $conditionValue, $order, $limit) {\n        // Prepare query using placeholders\n        $query = "SELECT $fields FROM $table WHERE id = ? ORDER BY $order LIMIT $limit";\n        \n        if ($stmt = $this->conn->prepare($query)) {\n            // Bind the condition value as an integer\n            $stmt->bind_param('i', $conditionValue);\n            $stmt->execute();\n            $result = $stmt->get_result();\n            \n            // Fetch all rows into an array\n            $data = mysqli_fetch_all($result, MYSQLI_ASSOC);\n            \n            $stmt->close();\n            return $data;\n        } else {\n            trigger_error('Error preparing statement: ' . $this->conn->error, E_USER_WARNING);\n            return [];\n        }\n    }\n\n}\n\n// Usage example:\n$connection = new MySQL();\n$extract = $connection->extracting_data("tablename", "id,name,points", $_GET['id'] ?? 0, "date desc", "10");