// Load the nusoap library\nrequire_once 'nusoap.php';\n\n// Create the soapclient object\n$client = new soapclient('http://www.example.com/your-wsdl-url?wsdl', true);\n\n// Specify parameters if needed as an associative array\n$params = array(\n    'param1' => 'value1',\n    'param2' => 'value2'\n);\n\n// Perform the SOAP call and check for errors\n$result = $client->call('YourFunctionName', $params);\nif ($client->fault) {\n    echo '<h2>Fault</h2><pre>';\n    print_r($result);\n    echo '</pre>';\n} else {\n    // Check if there were any error messages during the call\n    $err = $client->getError();\n    if ($err) {\n        echo '<h2>Error</h2><pre>' . htmlspecialchars($err) . '</pre>';\n    } else {\n        print_r($result);\n    }\n}