<?php\n$filename = 'counter.txt';\n\nif (!file_exists($filename)) {\n    file_put_contents($filename, 0);\n}\n\n$count = intval(file_get_contents($filename));\n?>\n\n<!DOCTYPE html>\n<html lang="en">\n<head>\n    <meta charset="UTF-8">\n    <title>Image Click Counter</title>\n    <link rel="stylesheet" href="style.css">\n    <script src="script.js"></script>\n</head>\n<body>\n    <div id="counter"><?php echo $count; ?></div>\n    <img src="image.png" alt="Clickable Image" id="clickableImage">\n</body>\n</html>