<?php\n\nfunction decrypt($data) {\n    $key = 'ThisIsASecretKey'; // Ensure this matches the key used in Java\n    $method = 'AES-128-CBC';\n    \n    // The IV (initialization vector) should also be sent with the encrypted data.\n    // Here, we assume it's part of the data, base64 encoded after encryption.\n    list($encrypted_data, $iv) = explode('::', base64_decode($data), 2);\n    \n    return trim(openssl_decrypt(base64_decode($encrypted_data), $method, $key, OPENSSL_RAW_DATA, base64_decode($iv)));\n}\n\n// Example usage with a sample encrypted string\n$encryptedDataFromJsp = 'base64EncodedEncryptedString::base64EncodedIv';\necho decrypt($encryptedDataFromJsp);\n\n?>