function return_json($obj) {\n    // Start output buffering\n    ob_start();\n\n    // Set the content type header for JSON response\n    header("Content-Type: application/json");\n\n    // Encode the object into JSON format and echo it\n    echo json_encode($obj);\n\n    // Get the current buffer contents, clean (erase) the buffer and turn off output buffering\n    $jsonOutput = ob_get_clean();\n\n    // Output the cleaned buffer\n    echo $jsonOutput;\n\n    // Exit to ensure no other code runs after this point\n    exit;\n}