// Using IMAP instead of POP3 due to better support and Gmail compatibility\n$mailbox = '{imap.gmail.com:993/imap/ssl}INBOX';\n$username = 'your-email@gmail.com';\n$password = 'your-password';\n\n// Open the mailbox\n$connection = imap_open($mailbox, $username, $password);\n\nif (!$connection) {\n    die('Failed to connect to the mail server: ' . imap_last_error());\n}\n\n// Get emails' index numbers\n$emailIndexes = imap_search($connection, 'ALL');\n\nif ($emailIndexes === false) {\n    echo "No messages found!";\n} else {\n    foreach ($emailIndexes as $emailIndex) {\n        $overview = imap_fetch_overview($connection, $emailIndex, 0);\n        $message = imap_body($connection, $emailIndex);\n\n        // Output some information about each email\n        echo "<h1>Subject: " . htmlspecialchars($overview[0]->subject) . "</h1>\n";\n        echo "<p>From: " . htmlspecialchars($overview[0]->from) . "</p>\n";\n        echo "<div>" . nl2br(htmlspecialchars($message)) . "</div>";\n    }\n}\n\n// Close the mailbox\nimap_close($connection);