function data_info($data)\n{\n    if ($data) {\n        $bufferArray = [];\n        while (!feof($data)) {\n            $buffer = trim(fgets($data)); // Use trim to remove any leading/trailing whitespace or newlines\n            if (file_exists($buffer)) {\n                $bufferArray[$buffer]['exists'] = true;\n                $bufferArray[$buffer]['readable'] = is_readable($buffer);\n                $bufferArray[$buffer]['writable'] = is_writable($buffer);\n                $bufferArray[$buffer]['size'] = filesize($buffer);\n            } else {\n                $bufferArray[$buffer]['exists'] = false;\n            }\n        }\n        fclose($data); // Close the file handle\n        print_r($bufferArray);\n    } else {\n        echo "The file could not be opened";\n    }\n}\n\n$data = fopen("D:/xampp/htdocs/Practice/ficheros.txt", "r");\nif ($data) {\n    data_info($data);\n} else {\n    echo "Could not open the file.";\n}