function multiPostRequest($urls, $postData) {\n    $mh = curl_multi_init();\n    $chs = [];\n\n    foreach ($urls as $i => $url) {\n        $chs[$i] = curl_init();\n        curl_setopt($chs[$i], CURLOPT_URL, $url);\n        curl_setopt($chs[$i], CURLOPT_POST, true);\n        curl_setopt($chs[$i], CURLOPT_POSTFIELDS, http_build_query($postData));\n        curl_setopt($chs[$i], CURLOPT_RETURNTRANSFER, true);\n\n        curl_multi_add_handle($mh, $chs[$i]);\n    }\n\n    $running = null;\n\n    do {\n        curl_multi_exec($mh, $running);\n        curl_multi_select($mh);\n    } while ($running > 0);\n\n    foreach ($chs as $ch) {\n        // Response from each request can be processed here\n        curlMultiResponse[] = curl_multi_getcontent($ch);\n        curl_multi_remove_handle($mh, $ch);\n        curl_close($ch);\n    }\n\n    curl_multi_close($mh);\n    \n    return curlMultiResponse;\n}\n\n// Example usage\n$urls = ['http://example.com/api', 'http://example2.com/api'];\n$postData = ['key' => 'value'];\n\n$responses = multiPostRequest($urls, $postData);