<?php\n// Generate a random token to store in the database\n$token = bin2hex(random_bytes(32));\n$userId = 1; // Assume this user ID comes from your authentication logic\n\n// Store the token and associated user id in your database\n$stmt = $pdo->prepare("INSERT INTO remember_me (user_id, token) VALUES (:user_id, :token)");\n$stmt->execute([':user_id' => $userId, ':token' => $token]);\n\n// Set cookie with HttpOnly and Secure flags\n$cookieOptions = [\n    'path'     => '/',\n    'domain'   => $_SERVER['HTTP_HOST'],\n    'secure'   => true,\n    'httponly' => true,\n    'samesite' => 'Strict',\n];\nsetcookie('remember_token', $token, time() + (86400 * 30), $cookieOptions); // 86400 = 1 day, 86400 * 30 = 30 days\n\n// On login, check if the cookie is set and use it to log in the user\nif (isset($_COOKIE['remember_token'])) {\n    $tokenFromCookie = $_COOKIE['remember_token'];\n    // Validate token against your database here...\n}\n?>