function outputscores($mysqlquery, $reverse = false) {\n    $rows = [];\n    while ($row = mysql_fetch_array($mysqlquery)) {\n        $rows[] = $row;\n    }\n\n    // Reverse the array if requested\n    if ($reverse) {\n        $rows = array_reverse($rows);\n    }\n\n    foreach ($rows as $row) {\n        echo '<img src="img/date.png" /> ' . date("j M Y", strtotime($row['timestamp'])) . '<br />';\n        echo '<img src="img/time.png" /> ' . date("H:i:s", strtotime($row['timestamp'])) . '<br />';\n        echo '<img src="img/star.png" /> ' . $row['score'] . $_GET["scoretype"] . '<br /><br />';\n    }\n}