$xmlString = '<?xml version="1.0" encoding="UTF-8"?>\n<targets>\n  <showcases>\n    <building name="Big Blue" />\n    <building name="Shiny Red" />\n    <building name="Mellow Yellow" />\n  </showcases>\n</targets>';\n\n$doc = new DOMDocument();\n$doc->loadXML($xmlString);\n\n// The name you are looking for\n$searchName = 'Shiny Red';\n\n// Get all building nodes\n$buildings = $doc->getElementsByTagName('building');\n\nforeach ($buildings as $building) {\n    if ($building->hasAttribute('name') && $building->getAttribute('name') === $searchName) {\n        echo "Building with name '$searchName' exists.\n";\n        exit;\n    }\n}\n\necho "No building with name '$searchName' found.\n";